import React, { useState, useEffect, Context } from "react";

const UserListContext = createContext();

const UserList = props => {
	const [username, setUsername] = useState([]);
	const [query, setQuery] = useState("");
	const [error, setError] = useState("");

	useEffect(() => {
		fetch("https://api.github.com/search/users?q=gizemcandemir")
			.then(githubUserObject => githubUserObject.json())
			.then(githubUserList => setUserList(githubUserList))
			.catch(err => {
				setError("Error");
			});
	}, [query]);

	const handleQueryChange = e => {
		setQuery(e.target.value);
	};

	return (
		<UserListContext.Provider
			value={{
				username,
				query,
				error,
				handleQueryChange
			}}
		>
			{props.children}
		</UserListContext.Provider>
	);
};

export { UserList, UserListContext };
