import React, { useState, useEffect, createContext } from "react";
import githubRequestSetting from "../githubSettings.js";
const UserContext = createContext();

const UserContextProvider = props => {
	const [loading, setLoading] = useState(true);
	const [query, setQuery] = useState("gizem");
	const [userList, setUserList] = useState([]);
	// const [error, setError] = useState("");

	// const BASEURL = "https://api.github.com/";
	const BASE_URL = "https://api.giphy.com/v1/gifs/search?";
	const APP_ID = "api_key=H8y0gVlIV1y5leb7InXZ28X880GEgcPS";
	const searchQuery = "&q=" + query;
	const limitQuery = "&limit=20";
	const searchUrl = BASE_URL + APP_ID + searchQuery + limitQuery;

	const handleQueryChange = e => {
		setQuery(e.target.value);
	};

	const fetchUsers = async query => {
    if (query !== "") {
      const url = `https://api.github.com/search/users?q=${query}`;
      setLoading(true);
      const users = await fetch(url, {
        headers: {
          Authorization:
            "Basic " +
            btoa(
              `${process.env.REACT_APP_GITHUB_NAME}:${process.env.REACT_APP_GITHUB_PW}`
            )
        }
      })
        .then(data => data.json())
        .catch(error => setError(error.message));

      setUsers(users.items);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers(query);
  }, [query]);

	return (
		<UserContext.Provider
			value={{
				loading,
				query,
				userList,
				handleQueryChange
			}}
		>
			{props.children}
		</UserContext.Provider>
	);
};

export { UserContextProvider, UserContext };
