import React, { useContext } from "react";
import "./App.css";
import { UserList } from "./UserList";
import { UserContext } from "./contexts/userContext";

function App(props) {
	const {handleQueryChange} = useContext(UserContext)
	return (
			<div className="center">
				<h1>Github user searcher</h1>
				<input placeholder="Search for user" onChange={handleQueryChange} />
				<UserList />
			</div>
	);
}

export default App;
