import React, { useState, useEffect } from "react";
import githubRequestSetting from "../helpers/githubSettings.js";

const SearchContext = React.createContext();

const SearchContextProvider = props => {
	const [loading, setLoading] = useState(true);
	const [query, setQuery] = useState("");
	const [userList, setUserList] = useState([]);
	const [error, setError] = useState("");

	const handleQueryChange = e => {
		console.log(e.target.value);
		setQuery(e.target.value);
		console.log(query);
	};

	useEffect(() => {
		if (query !== "") {
			const url = `https://api.github.com/search/users?q=${query}`;
			setLoading(true);
			fetch(url, githubRequestSetting)
				.then(data => console.log(`hdhhddh`, data.json()))
				.then(jsonResponse => setUserList(jsonResponse.items))
				.catch(error => setError(error.message));
			setLoading(false);
		}
	}, [query]);

	return (
		<SearchContextProvider
			value={{ loading, query, userList, handleQueryChange }}
		>
			{props.children}
		</SearchContextProvider>
	);
};

export { SearchContextProvider, SearchContext };
